The 7 Space Technical Status:
- Booking System: ${THE_7_SPACE_BOOKING_STATUS}
- Payment Processing: ${THE_7_SPACE_PAYMENT_STATUS}
- Gallery Management: ${THE_7_SPACE_GALLERY_STATUS}
- Wellness Scheduling: ${THE_7_SPACE_WELLNESS_STATUS}
- Artist Portal: ${THE_7_SPACE_ARTIST_STATUS}

Monitoring: http://localhost:3000/d/the7space-overview

A.M. Consulting Technical Status:
- Client Portal: ${AM_CONSULTING_PORTAL_STATUS}
- Retreat Management: ${AM_CONSULTING_RETREAT_STATUS}
- Practitioner Network: ${AM_CONSULTING_NETWORK_STATUS}
- Assessment Tools: ${AM_CONSULTING_ASSESSMENT_STATUS}
- Communication Hub: ${AM_CONSULTING_COMM_STATUS}

Dashboard: http://localhost:3000/d/amconsulting-overview

HigherSelf Platform Status:
- Community Directory: ${HIGHERSELF_DIRECTORY_STATUS}
- Learning Management: ${HIGHERSELF_LMS_STATUS}
- Event Platform: ${HIGHERSELF_EVENTS_STATUS}
- Member Portal: ${HIGHERSELF_PORTAL_STATUS}
- Resource Library: ${HIGHERSELF_LIBRARY_STATUS}

Dashboard: http://localhost:3000/d/higherself-overview

curl -X POST "http://localhost:8000/api/v1/integrate" \
-H "Authorization: Bearer $(op read 'op://HigherSelf/HSN-API/token')" \
-H "Content-Type: application/json" \
-d '{
  "business_id": "",
  "integration_type": "",
  "config": {}
}'

# MongoDB credentials
export MONGODB_USERNAME=$(op read "op://HigherSelf/MongoDB/username")
export MONGODB_PASSWORD=$(op read "op://HigherSelf/MongoDB/password")

# Redis credentials
export REDIS_PASSWORD=$(op read "op://HigherSelf/Redis/password")

# Connection string with credentials
export MONGODB_URI="mongodb://$(op read 'op://HigherSelf/MongoDB/username'):$(op read 'op://HigherSelf/MongoDB/password')@localhost:27017/higherself"

# Retrieve webhook secrets
export WEBHOOK_SECRET=$(op read "op://HigherSelf/Webhooks/secret")
export GITHUB_WEBHOOK_SECRET=$(op read "op://HigherSelf/GitHub/webhook-secret")
export ZAPIER_WEBHOOK_SECRET=$(op read "op://HigherSelf/Zapier/webhook-secret")

# Verify webhook signature
echo "Webhook secrets loaded for signature verification"

# Grafana credentials
export GRAFANA_USER=$(op read "op://HigherSelf/Grafana/username")
export GRAFANA_PASS=$(op read "op://HigherSelf/Grafana/password")

# Prometheus credentials
export PROMETHEUS_USER=$(op read "op://HigherSelf/Prometheus/username")
export PROMETHEUS_PASS=$(op read "op://HigherSelf/Prometheus/password")

# Open monitoring with credentials
open "http://${GRAFANA_USER}:${GRAFANA_PASS}@localhost:3000"

curl -X PATCH "https://api.airtable.com/v0/$(op read 'op://HigherSelf/Airtable/base-id')/$(op read 'op://HigherSelf/Airtable/table-name')/[RECORD_ID]" \
-H "Authorization: Bearer $(op read 'op://HigherSelf/Airtable/api-key')" \
-H "Content-Type: application/json" \
-d '{
  "fields": {
    "Last Updated": "'$(date -u +%Y-%m-%dT%H:%M:%SZ)'",
    "Updated By": "HigherSelf Network Server"
  }
}'

curl -X POST "https://api.notion.com/v1/pages" \
-H "Authorization: Bearer $(op read 'op://HigherSelf/Notion/api-token')" \
-H "Content-Type: application/json" \
-H "Notion-Version: 2022-06-28" \
-d '{
  "parent": {
    "database_id": "$(op read 'op://HigherSelf/Notion/database-id')"
  },
  "properties": {
    "Name": {
      "title": [
        {
          "text": {
            "content": "[Page Title]"
          }
        }
      ]
    }
  }
}'
